import React,{useState} from 'react'
import { Grid, Paper, Typography, TextField, Button } from '@material-ui/core'
import axios from "axios";
import Select from '@material-ui/core/Select';

const leaveTypes = [
    {
        label: "Sick Leave",
    },

    {
        label: "Monthly leave",
        value: "20 Mbps",
    },
];
const handoverStaffs = [
    {
        label: "Manager",
        value: "5 Mbps",
    },
    {
        label: "Supervisor",
        value: "10 Mbps",
    },
];
export default function ApplicationForm() {
    const paperStyle = { padding: '30px 20px', height: '500px', width: '500px', margin: "20px auto" }
    const headerStyle = { margin: 0, fontWeight:"800" }
    const marginTop = { marginTop: 15 }
    const [customer, setCustomer] = useState({
        name: "",
        email: "",
        leaveType:"",
        leaveDays: "",
        leaveEndDate:"",
        handoverStaff:"",
        handoverReport:"",
        comments: "",
       
    });

    const createApplication= () => {
        axios.post("http://localhost:5000/applications", application).then(() => {
            window.alert("Your request has been received.Successfully applied for Leave.")
            window.location.reload(false);
        })
    };


    return (
        <Grid>
            <Paper elevation={20} style={paperStyle}>
                <Grid align='center'>
                <h2 style={headerStyle}>Application Form</h2>
                    <Typography variant='caption' gutterBottom>Please fill in the details below.</Typography>
                </Grid>
                <form>
                    <TextField style={marginTop} id="name" value={customer.name} onChange={(e) => setApplication({...application, name: e.target.value })}
                        fullWidth label='Name' placeholder="Enter your name" />
                    <TextField style={marginTop} id="email" value={customer.email} onChange={(e) => setApplication({ ...application,email: e.target.value })}
                        fullWidth label='Email' placeholder="Enter your email" />
                    <TextField style={marginTop} id="leaveEndDate" value={customer.leaveEndDate} onChange={(e) => setApplication({ ...application,leaveEndDate: e.target.value })}
                        fullWidth label='End Date' placeholder="Enter your phone number" />
                    <TextField style={marginTop} id="location" value={customer.leaveDays} onChange={(e) => setApplication({...application,leaveDays: e.target.value })}
                        fullWidth label='Location' placeholder="Apartment,City" />
                    
                    <Select style={marginTop} id="handoverStaff" value={customer.handoverStaff} onChange={(e) => setApplication({ ...application,handoverStaff: e.target.value })}
                        
                        fullWidth label="Su" placeholder="Handover Staff" >
                            {handoverStaffs.map((handoverStaff) => (
                                <option value={handoverStaff.value}>{handoverStaff.label}</option>
                            ))}
                      
                    </Select>
                    <Select style={marginTop} id="leaveType" value={customer.leaveType} onChange={(e) => setApplication({ ...application,leaveType: e.target.value })}
                        
                        fullWidth label="Type of Leave" placeholder="Type of Leave" >
                            {leaveTypes.map((leaveType) => (
                                <option value={leaveType.value}>{leaveType.label}</option>
                            ))}
                      
                    </Select>
                    <Button type='submit' variant='contained' color='primary' style={marginTop} onClick={createApplication}>Submit</Button>
                </form>
            </Paper>
        </Grid>
    )
}
